mapper.select:

/*	retrieveMaxBoardNo	*/
	SELECT MAX(BOARD.BNO)
	FROM   tb_jw_comm BOARD

mapper.insert:

INSERT INTO TB_JW_COMM(
	BNO
	, TITLE
	, CONTENT
	, WRITER
) VALUES (
	SEQ_COMM.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:

/*	updateBoard	*/
	UPDATE tb_jw_comm
	SET TITLE = #{title}
	, CONTENT = #{content}
	WHERE BNO = #{bno}


/*	updateBoardHits	*/
	UPDATE tb_jw_comm
	SET BOARD_HITS          =  BOARD_HITS + 1
	WHERE BNO = #{BNO}

mapper.delete:

/*	deleteBoard	*/
	DELETE
	FROM  tb_jw_comm
	WHERE 1=1
	AND BNO = #{BNO}

mapper.select:


	SELECT
	    BNO, TITLE, CONTENT, WRITER, REGDATE
    from(
	    SELECT /*+INDEX_DESC(tb_jw_comm pk_comm) */
	        ROWNUM rn, bno, title, content, writer, regdate
	    FROM tb_jw_comm
	    WHERE 






							title like '%'||#{keyword}||'%'


							content like '%'||#{keyword}||'%'


							writer like '%'||#{keyword}||'%'






		ROWNUM <= #{pageNum} * #{amount}
		)
	where RN > (#{pageNum} -1) * #{amount}
